import java.util.*;

public class Main{
	public static void main(String[] args){
		
		String sampel = "";
		
		char[] buffer = new char[1000];
		
		for(int i = 0; i  < buffer.length ;i++){
			buffer[i] = sampel.charAt(i);
		}
		
		int index = 0;
		int value = 1;
		long result = 0;
		long tempResult = 1;
		for(char number: buffer){
			
			int theNumber = Integer.parseInt(Character.toString(number));
			//System.out.println(theNumber);
			if(theNumber == 0){
				tempResult = 1;
				value = 1;
				index++;
				continue;
			}
			
			tempResult *= theNumber;
			
			if(value == 13){
				if(tempResult > result){
					result = tempResult;
				}
				//System.out.println(tempResult + "/" + Integer.parseInt(Character.toString(buffer[index - 3])));
				tempResult /= Integer.parseInt(Character.toString(buffer[index - 12]));
				value--;
			}
			
			//System.out.println("\n");
			value++;
			index++;
		}
		
		System.out.println(result);
		
	}
}
